import "./App.css";
import { NavBar } from "./components/NavBar";

import { BrowserRouter as Router, Routes, Route} from "react-router-dom";
import { Home } from "./pages/Home";
import { Grid } from "./pages/Grid";
import { Detail } from "./pages/Detail";
import { useState } from "react";
import { faSearch,faHouseChimney } from "@fortawesome/free-solid-svg-icons";

export default function MoviePop() {
  const [section, setSection] = useState("Home")
  const [icons, setIcons] = useState([faSearch, faHouseChimney])

  function cambiarDibujo(){
    if(section ==="Home"){
      setIcons([faSearch, faHouseChimney])
    }
  }
  return (
    <div className="App">
      <Router>
      <div>
        <NavBar/>
        <main>
          <Routes>
            <Route path="/" element={<Home/>}></Route>
            <Route path="/buscar" element={<Grid/>}></Route>
            <Route exact path="/pelicula/:peliculaId" element={<Detail/>}></Route>
          </Routes>
        </main>
      </div>
    </Router>
    </div>
  );
}
