import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { Link } from "react-router-dom";
import {
  faArrowLeft,
  faHouseChimney,
  faSearch,
  faUser,
} from "@fortawesome/free-solid-svg-icons";

import styles from "../components styles/NavBar.module.css";
import { useEffect, useState } from "react";

export function NavBar({ page }) {
  const [styleButton, setStyleButton] = useState([0, 1, 0]);
  const [buttonBack, setButtonBack] = useState(faSearch);

  function styleActive(is_active) {
    if (is_active) {
      return styles.colorA;
    }
    return styles.colorB;
  }

  useEffect(() => {
    if (page === "Home") {
      setStyleButton([0, 1, 0]);
      setButtonBack(faSearch);
    } else if (page === "Grid") {
      setStyleButton([0, 0, 1]);
      setButtonBack(faSearch);
    } else if (page === "Profile") {
      setStyleButton([1, 0, 0]);
      setButtonBack(faSearch);
    } else if (page === "Detail") {
      setButtonBack(faArrowLeft);
    }
  }, [page]);

  return (
    <Router>
    <main>
      <Routes>
        <Route path="/" element={<Home/>}></Route>
        <Route path="/buscar/" element={<Grid/>}></Route>
        <Route exact path="/pelicula/:peliculaId" element={<Detail/>}></Route>
        <Route path="/login/" element={<Login/>}></Route>
      </Routes>
    </main>
</Router>
