import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { Link, useLocation, useNavigate, useParams } from "react-router-dom";
import { faHouseChimney, faSearch } from "@fortawesome/free-solid-svg-icons";

import styles from "../components styles/NavBar.module.css";
import { useState } from "react";

export function NavBar({ key }) {
  const [styleButton, setStyleButton] = useState([
    styles.colorA,
    styles.colorB,
  ]);

  function styleNav() {
    if (key === 1) {
      setStyleButton([styles.colorA, styles.colorB]);
    }
    if (key === 2) {
      setStyleButton([styles.colorB, styles.colorA]);
    }
  }
  return (
    <nav className={styles.nav}>
      <ul className={styles.navList}>
        <li className={styles.navItem}>
          <Link to="/Buscar">
            <button
              className={styles.navButton + " " + styleButton[1]}
              onClick={() => setStyleButton([styles.colorB, styles.colorA])}
            >
              <FontAwesomeIcon icon={icono} size={"2x"} />
            </button>
          </Link>
        </li>
        <li className={styles.navItem}>
          <Link to="/">
            <button
              className={styles.navButton + " " + styleButton[0]}
              onClick={() => setStyleButton([styles.colorA, styles.colorB])}
            >
              <FontAwesomeIcon icon={faHouseChimney} size={"2x"} />
            </button>
          </Link>
        </li>
      </ul>
    </nav>
  );
}
