import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { Link } from "react-router-dom";
import {
  faArrowLeft,
  faHouseChimney,
  faSearch,
  faUser,
} from "@fortawesome/free-solid-svg-icons";

import styles from "../components styles/NavBar.module.css";
import { useEffect, useState } from "react";

export function NavBar({ page }) {
  const [styleButton, setStyleButton] = useState([0, 1, 0]);
  const [buttonBack, setButtonBack] = useState(faSearch);

  function styleActive(is_active) {
    if (is_active) {
      return styles.colorA;
    }
    return styles.colorB;
  }

  useEffect(() => {
    if (page === "Home") {
      setStyleButton([0, 1, 0]);
      setButtonBack(faSearch);
    } else if (page === "Grid") {
      setStyleButton([0, 0, 1]);
      setButtonBack(faSearch);
    } else if (page === "Profile") {
      setStyleButton([1, 0, 0]);
      setButtonBack(faSearch);
    } else if (page === "Detail") {
      setButtonBack(faArrowLeft);
    }
  }, [page]);

  return (
    <nav className={styles.nav}>
      <ul className={styles.navList}>
        <li>
          <Link to="/buscar/">
            <button
              className={styles.navButton + " " + styleActive(styleButton[2])}
            >
              <FontAwesomeIcon icon={faSearch} size={"2x"} />
            </button>
          </Link>
        </li>
        <li>
          <Link to="/">
            <button
              className={styles.navButton + " " + styleActive(styleButton[1])}
            >
              <FontAwesomeIcon icon={faHouseChimney} size={"2x"} />
            </button>
          </Link>
        </li>
        <li>
          <Link to="/login/">
            <button
              className={styles.navButton + " " + styleActive(styleButton[0])}
            >
              <FontAwesomeIcon icon={faUser} size={"2x"} />
            </button>
          </Link>
        </li>
      </ul>
    </nav>
  );
}
